config.styles:
/* Minimal template styles */
body {
  font-family: 'Georgia', serif;
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  line-height: 1.8;
}

.export-header {
  border: none;
  padding: 0;
  margin-bottom: 3rem;
}

.export-header h1 {
  font-size: 2rem;
  font-weight: normal;
  color: #000;
  border-bottom: 1px solid #ddd;
  padding-bottom: 0.5rem;
}

.step {
  background: transparent;
  border: none;
  padding: 0;
  margin-bottom: 3rem;
}

.step h3 {
  font-size: 1.2rem;
  font-weight: normal;
  font-style: italic;
  color: #555;
  margin-bottom: 1rem;
}

.screenshot img {
  border: none;
  box-shadow: none;
  max-width: 600px;
  margin: 2rem auto;
  display: block;
}
