config.styles:
/* Story mode styles */
@import url('https://fonts.googleapis.com/css2?family=Merriweather:wght@300;400;700&family=Source+Sans+Pro:wght@400;600&display=swap');

body {
  font-family: 'Merriweather', serif;
  background: linear-gradient(to bottom, #f8f9fa, #e9ecef);
  color: #2c3e50;
  line-height: 1.8;
}

.export-header {
  text-align: center;
  padding: 4rem 2rem;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  position: relative;
  overflow: hidden;
}

.export-header::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url('data:image/svg+xml,<svg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"><g fill="none" fill-rule="evenodd"><g fill="%23ffffff" fill-opacity="0.05"><polygon points="30 30 0 0 60 0"/></g></g></svg>') repeat;
  opacity: 0.3;
}

.export-header h1 {
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  position: relative;
  z-index: 1;
  text-shadow: 2px 2px 4px rgba(0,0,0,0.2);
}

.export-header .description {
  font-size: 1.4rem;
  font-family: 'Source Sans Pro', sans-serif;
  opacity: 0.95;
  max-width: 800px;
  margin: 0 auto;
  position: relative;
  z-index: 1;
}

.table-of-contents {
  background: white;
  padding: 2rem;
  margin: -2rem auto 3rem;
  max-width: 800px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.1);
  border-radius: 16px;
  position: relative;
}

.table-of-contents h2 {
  text-align: center;
  color: #667eea;
  font-size: 1.8rem;
  margin-bottom: 1.5rem;
}

.table-of-contents ol {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1rem;
  padding: 0;
  list-style: none;
}

.table-of-contents li {
  padding: 1rem;
  background: #f8f9fa;
  border-radius: 8px;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.table-of-contents li:hover {
  transform: translateY(-2px);
  box-shadow: 0 5px 15px rgba(102, 126, 234, 0.2);
}

.table-of-contents a {
  color: #667eea;
  font-weight: 600;
  font-family: 'Source Sans Pro', sans-serif;
  display: flex;
  align-items: center;
}

.table-of-contents a::before {
  content: counter(list-item);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  background: #667eea;
  color: white;
  border-radius: 50%;
  margin-right: 12px;
  font-size: 0.9rem;
}

.export-content {
  max-width: 900px;
  margin: 0 auto;
  padding: 0 2rem;
}

.steps h2 {
  text-align: center;
  font-size: 2.5rem;
  margin-bottom: 3rem;
  position: relative;
}

.steps h2::after {
  content: '';
  display: block;
  width: 80px;
  height: 4px;
  background: linear-gradient(to right, #667eea, #764ba2);
  margin: 1rem auto;
  border-radius: 2px;
}

.step {
  background: white;
  margin-bottom: 4rem;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.08);
  position: relative;
}

.step::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 6px;
  height: 100%;
  background: linear-gradient(to bottom, #667eea, #764ba2);
}

.step-content {
  padding: 2.5rem;
}

.step h3 {
  font-size: 1.8rem;
  color: #2c3e50;
  margin-bottom: 1.5rem;
  padding-left: 1rem;
  position: relative;
}

.step h3::before {
  content: counter(step-counter);
  position: absolute;
  left: -3rem;
  top: 50%;
  transform: translateY(-50%);
  width: 48px;
  height: 48px;
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
}

.screenshot {
  position: relative;
  margin: 0;
  background: #f8f9fa;
}

.screenshot::before {
  content: 'üì∏';
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.2rem;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.screenshot img {
  width: 100%;
  height: auto;
  display: block;
  border: none;
  box-shadow: none;
  border-radius: 0;
}

.timestamp {
  padding: 1rem 2.5rem;
  background: #f8f9fa;
  font-style: italic;
  color: #6c757d;
  font-size: 0.9rem;
  border-top: 1px solid #e9ecef;
}

.timestamp::before {
  content: 'üïê ';
  margin-right: 0.5rem;
}

.export-footer {
  text-align: center;
  padding: 3rem 2rem;
  margin-top: 4rem;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
}

.export-footer p {
  font-family: 'Source Sans Pro', sans-serif;
  font-size: 1.1rem;
  opacity: 0.9;
}

/* Chapter dividers */
.chapter-divider {
  text-align: center;
  margin: 4rem 0;
  position: relative;
}

.chapter-divider::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(to right, transparent, #667eea, transparent);
}

.chapter-divider span {
  background: white;
  padding: 0 2rem;
  position: relative;
  color: #667eea;
  font-weight: 600;
  font-family: 'Source Sans Pro', sans-serif;
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .export-header h1 {
    font-size: 2.5rem;
  }

  .export-header .description {
    font-size: 1.2rem;
  }

  .step-content {
    padding: 1.5rem;
  }

  .table-of-contents ol {
    grid-template-columns: 1fr;
  }

  .step h3 {
    font-size: 1.5rem;
    padding-left: 3rem;
  }

  .step h3::before {
    left: 0;
  }
}
