config.styles:
@page {
  size: A4 landscape;
  margin: 0;
}

.step {
  page-break-after: always;
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
}

.step h3 {
  font-size: 48pt;
  margin-bottom: 2rem;
}

.screenshot img {
  max-width: 90vw;
  max-height: 60vh;
  object-fit: contain;
}
