config.styles:
@page {
  size: A4;
  margin: 2cm;
  @bottom-center {
    content: "Page " counter(page) " of " counter(pages);
    font-size: 9pt;
    color: #666;
    border-top: 1px solid #ddd;
    padding-top: 5mm;
    width: 100%;
  }
  @top-center {
    content: string(doc-title);
    font-size: 10pt;
    color: #2563eb;
    font-weight: 600;
    padding-bottom: 5mm;
    width: 100%;
  }
}

h1 {
  string-set: doc-title content();
  page-break-before: always;
}

code {
  font-family: 'Courier New', monospace;
  background-color: #f5f5f5;
  padding: 2px 4px;
  border-radius: 3px;
  font-size: 0.9em;
}

pre {
  background-color: #f8f8f8;
  border: 1px solid #ddd;
  padding: 10px;
  border-radius: 5px;
  overflow-x: auto;
  white-space: pre-wrap;
  word-wrap: break-word;
}

pre code {
  background: none;
  padding: 0;
  font-size: 0.9em;
}

.screenshot {
  page-break-inside: avoid;
  margin: 20px 0;
  text-align: center;
}

.screenshot img {
  max-width: 100%;
  height: auto;
  border: 1px solid #ddd;
  border-radius: 5px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.1);
}

.diagram {
  page-break-inside: avoid;
  text-align: center;
  margin: 20px 0;
}

.table-wrapper {
  page-break-inside: avoid;
  margin: 20px 0;
}

table {
  width: 100%;
  border-collapse: collapse;
  margin: 20px 0;
}

table th {
  background-color: #f0f0f0;
  font-weight: 600;
  text-align: left;
  padding: 8px;
  border: 1px solid #ddd;
}

table td {
  padding: 8px;
  border: 1px solid #ddd;
  vertical-align: top;
}

.note {
  background-color: #f0f7ff;
  border-left: 4px solid #2563eb;
  padding: 15px;
  margin: 20px 0;
  font-style: italic;
}

.warning {
  background-color: #fff8e6;
  border-left: 4px solid #f59e0b;
  padding: 15px;
  margin: 20px 0;
}

.error {
  background-color: #fef2f2;
  border-left: 4px solid #ef4444;
  padding: 15px;
  margin: 20px 0;
}
